mapper.select:
SELECT LOGINID 
	FROM MEMBER 
WHERE LOGINID=#{loginId} AND LOGINPW=#{loginPw}

SELECT COUNT(LOGINID)
	FROM MEMBER 
WHERE LOGINID=#{loginId}

mapper.update:
UPDATE MEMBER 
	SET LOGINPW=#{loginPw}, NAME=#{name}, EMAIL=#{email} 
WHERE LOGINID=#{loginId}

UPDATE MEMBER
	SET LASTLOGIN=SYSDATE 
WHERE LOGINID=#{loginId}
